import UIKit

public class CalendarDesignKit : NSObject {

    //// Cache

    private struct Cache {
        static var foregroundColor: UIColor = UIColor(red: 0.907, green: 0.190, blue: 0.190, alpha: 1.000)
        static var backgroundColor: UIColor = UIColor(red: 0.652, green: 0.800, blue: 0.320, alpha: 1.000)
    }

    //// Colors

    public class var foregroundColor: UIColor { return Cache.foregroundColor }
    public class var backgroundColor: UIColor { return Cache.backgroundColor }

    //// Drawing Methods

    public class func drawTodayBackground(frame frame: CGRect) {

        //// DateCircle Drawing
        let dateCirclePath = UIBezierPath(ovalInRect: CGRectMake(frame.minX + 8, frame.minY + 8, 42, 42))
        CalendarDesignKit.foregroundColor.setFill()
        dateCirclePath.fill()
    }

    public class func drawWeekdayBackground(frame frame: CGRect) {

        //// DateCircle Drawing
        let dateCirclePath = UIBezierPath(ovalInRect: CGRectMake(frame.minX + 8, frame.minY + 8, 42, 42))
        UIColor.redColor().setStroke()
        dateCirclePath.lineWidth = 3
        dateCirclePath.stroke()
    }

    public class func drawWeekendBackground(frame frame: CGRect) {
        //// Color Declarations
        let color = UIColor(red: 0.682, green: 0.658, blue: 0.658, alpha: 1.000)

        //// DateCircle Drawing
        let dateCirclePath = UIBezierPath(ovalInRect: CGRectMake(frame.minX + 8, frame.minY + 8, 42, 42))
        color.setStroke()
        dateCirclePath.lineWidth = 3
        dateCirclePath.stroke()
    }

}

